function CraftingContractor:canDoMediumQuests(pPlayer, profession)
	if (pPlayer == nil) then
		return false
	end

	if (profession == "architect") then
		return CreatureObject(pPlayer):hasSkill("crafting_architect_production_02") or CreatureObject(pPlayer):hasSkill("crafting_architect_techniques_02")
			or CreatureObject(pPlayer):hasSkill("crafting_architect_harvesting_02") or CreatureObject(pPlayer):hasSkill("crafting_architect_blueprints_02")
	elseif (profession == "armorsmith") then
		return CreatureObject(pPlayer):hasSkill("crafting_armorsmith_personal_02") or CreatureObject(pPlayer):hasSkill("crafting_armorsmith_heavy_02")
			or CreatureObject(pPlayer):hasSkill("crafting_armorsmith_deflectors_02")
	elseif (profession == "artisan") then
		return CreatureObject(pPlayer):hasSkill("crafting_artisan_engineering_02") or CreatureObject(pPlayer):hasSkill("crafting_artisan_domestic_02")
	elseif (profession == "chef") then
		return CreatureObject(pPlayer):hasSkill("crafting_chef_dish_02") or CreatureObject(pPlayer):hasSkill("crafting_chef_dessert_02")
			or CreatureObject(pPlayer):hasSkill("crafting_chef_drink_02")
	elseif (profession == "droidengineer") then
		return CreatureObject(pPlayer):hasSkill("crafting_droidengineer_production_02") or CreatureObject(pPlayer):hasSkill("crafting_droidengineer_techniques_02")
			or CreatureObject(pPlayer):hasSkill("crafting_droidengineer_refinement_02") or CreatureObject(pPlayer):hasSkill("crafting_droidengineer_blueprints_02")
	elseif (profession == "tailor") then
		return CreatureObject(pPlayer):hasSkill("crafting_tailor_casual_02") or CreatureObject(pPlayer):hasSkill("crafting_tailor_field_02")
			or CreatureObject(pPlayer):hasSkill("crafting_tailor_formal_02")
	elseif (profession == "weaponsmith") then
		return CreatureObject(pPlayer):hasSkill("crafting_weaponsmith_firearms_02") or CreatureObject(pPlayer):hasSkill("crafting_weaponsmith_melee_02")
			or CreatureObject(pPlayer):hasSkill("crafting_weaponsmith_munitions_02")
	end

	return false
end

