function CraftingContractor:getContractBox(pPlayer)
	local pInventory = SceneObject(pPlayer):getSlottedObject("inventory")

	if (pInventory == nil) then
		return nil
	end

	for i = 0, SceneObject(pInventory):getContainerObjectsSize() - 1, 1 do
		local pItem = SceneObject(pInventory):getContainerObject(i)

		if pItem ~= nil then
			local objectPath = SceneObject(pItem):getTemplateObjectPath()

			if (string.find(objectPath, "object/tangible/container/quest/crafting_contract") ~= nil) then
				return pItem
			end
		end
	end

	return nil
end

function CraftingContractor:hasContractBox(pPlayer)
	return self:getContractBox(pPlayer) ~= nil
end

function CraftingContractor:hasFullContractBox(pPlayer)
	local pCrate = self:getContractBox(pPlayer)

	if (pCrate == nil) then
		return false
	end

	local crateObject = LuaContractCrate(pCrate)

	return crateObject:isCompleted()
end

function CraftingContractor:removeContractBox(pPlayer)
	local pCrate = self:getContractBox(pPlayer)

	if (pCrate == nil) then
		return
	end

	SceneObject(pCrate):destroyObjectFromWorld()
	SceneObject(pCrate):destroyObjectFromDatabase()
end

