function CraftingContractor:canTakeQuest(pPlayer)
	local lastOffer = readData(SceneObject(pPlayer):getObjectID() .. ":craftingContractor:lastJobOffer")
	return lastOffer == 0 or os.difftime(os.time(), lastOffer) > 60
end

function CraftingContractor:pickRandomQuest(pPlayer)
	local jobProf = self:getQuestProfession(pPlayer)
	local jobDiff = self:getQuestDifficulty(pPlayer)

	local questTable = self.quests[jobProf][jobDiff]

	if (questTable == nil) then
		printLuaError("CraftingContractor:pickRandomQuest grabbed nil questTable with profession " .. jobProf .. " and difficulty " .. jobDiff)
		return false
	end

	local pGhost = CreatureObject(pPlayer):getPlayerObject()

	if (pGhost == nil) then
		return false
	end

	local questSchematic
	local grabAttempts = 0
	local questNum

	repeat
		grabAttempts = grabAttempts + 1
		questNum = getRandomNumber(1, #questTable)

		questSchematic = questTable[questNum][1]
	until (PlayerObject(pGhost):hasSchematic(questSchematic) or grabAttempts >= 100)

	if (grabAttempts >= 100) then
		printLuaError("Failed to get random crafting quest using profession " .. jobProf .. " and difficulty " .. jobDiff)
		return false
	end

	self:setQuestNumber(pPlayer, questNum)
	return true
end

function CraftingContractor:getQuestData(pPlayer)
	local questNum = self:getQuestNumber(pPlayer)

	if (questNum == 0) then
		printLuaError("CraftingContractor:getQuestData returned invalid quest number.")
		return
	end

	local jobProf = self:getQuestProfession(pPlayer)
	local jobDiff = self:getQuestDifficulty(pPlayer)

	local questTable = self.quests[jobProf][jobDiff][questNum]

	if (questTable == nil) then
		printLuaError("CraftingContractor:getQuestData returned invalid quest data using profession " .. jobProf .. ", difficulty " .. jobDiff .. ", and quest number " .. questNum .. ".")
		return nil
	end

	return questTable
end

function CraftingContractor:setupCrate(pPlayer, pCrate)
	if (pCrate == nil or pPlayer == nil) then
		return
	end

	local questData = CraftingContractor:getQuestData(pPlayer)

	local crateObject = LuaContractCrate(pCrate)
	crateObject:parseDraftSchematic(questData[1])
	crateObject:setRequiredItem(questData[2])
	crateObject:setAmountNeeded(self:getAmountToCraft(pPlayer))

	local crateDiff = self:getQuestDifficulty(pPlayer)

	crateObject:setCrateDifficulty(crateDiff)

	local xpType = self:getExperienceType(pPlayer)

	crateObject:setXpType(xpType)

	self:resetVariables(pPlayer)
end

function CraftingContractor:setQuestProfession(pPlayer, profession)
	writeStringSharedMemory(SceneObject(pPlayer):getObjectID() .. ":craftingContractor:profession", profession)
end

function CraftingContractor:getQuestProfession(pPlayer)
	return readStringSharedMemory(SceneObject(pPlayer):getObjectID() .. ":craftingContractor:profession")
end

function CraftingContractor:setQuestDifficulty(pPlayer, difficulty)
	writeData(SceneObject(pPlayer):getObjectID() .. ":craftingContractor:difficulty", difficulty)
end

function CraftingContractor:getQuestDifficulty(pPlayer)
	return readData(SceneObject(pPlayer):getObjectID() .. ":craftingContractor:difficulty")
end

function CraftingContractor:setAmountToCraft(pPlayer, amount)
	writeData(SceneObject(pPlayer):getObjectID() .. ":craftingContractor:amountToCraft", amount)
end

function CraftingContractor:getAmountToCraft(pPlayer)
	return readData(SceneObject(pPlayer):getObjectID() .. ":craftingContractor:amountToCraft")
end

function CraftingContractor:setQuestNumber(pPlayer, num)
	writeData(SceneObject(pPlayer):getObjectID() .. ":craftingContractor:questNumber", num)
end

function CraftingContractor:getQuestNumber(pPlayer)
	return readData(SceneObject(pPlayer):getObjectID() .. ":craftingContractor:questNumber")
end

function CraftingContractor:resetVariables(pPlayer)
	local playerID = SceneObject(pPlayer):getObjectID()
	deleteStringSharedMemory(playerID .. ":craftingContractor:profession")
	deleteData(playerID .. ":craftingContractor:difficulty")
	deleteData(playerID .. ":craftingContractor:amountToCraft")
	deleteData(playerID .. ":craftingContractor:questNumber")
end
